-- FidelityCard data insertion :


WITH AvailableFidelityCards AS (
    SELECT fidelity_card_id,
           ROW_NUMBER() OVER (ORDER BY fidelity_card_id) AS rownum
    FROM Fidelity_Card
    WHERE fidelity_card_id NOT IN (SELECT fidelity_card_id FROM Customer WHERE fidelity_card_id IS NOT NULL)
    LIMIT 400
    ),
    CustomersToUpdate AS (
        SELECT id,
            ROW_NUMBER() OVER (ORDER BY id) AS rownum
        FROM Customer
        WHERE fidelity_card_id IS NULL
            LIMIT 400
    ),
    UpdatePairs AS (
        SELECT c.id, f.fidelity_card_id
        FROM CustomersToUpdate c
            JOIN AvailableFidelityCards f ON c.rownum = f.rownum
    )
UPDATE Customer c
SET fidelity_card_id = u.fidelity_card_id
    FROM UpdatePairs u
WHERE c.id = u.id;

UPDATE Fidelity_Card fc
SET points = 0,balance = 0
FROM Customer c
WHERE fc.fidelity_card_id = c.fidelity_card_id
AND c.enabled = FALSE;

